mapper.select:
select seq, subject, readCount, fk_perno, name, regDate ,fileName, orgFilename, fileSize, fk_subno from ( select rno, seq, subject, readCount, fk_perno, P.name as name, B.regDate as regDate ,fileName, orgFilename, fileSize, fk_subno from ( select row_number() over(order by seq desc) AS rno, seq, subject, readCount, fk_perno , to_char(regDate, 'yyyy-mm-dd') as regDate ,fileName, orgFilename, fileSize, fk_subno from tbl_classnoti where fk_subno = #{fk_subno}
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
)B join tbl_person P on P.perno = B.fk_perno ) where rno between #{startRno} and #{endRno}

select previousseq, previoussubject , seq, fk_perno, subject, name, content, readcount, B.regDate as regDate , nextseq , nextsubject , fileName, orgFilename, fileSize, fk_subno from ( select lag(seq,1) over(order by seq desc) AS previousseq , lag(subject,1) over(order by seq desc) AS previoussubject
, seq, fk_perno, subject, content, readcount , to_char(regDate, 'yyyy-mm-dd') as regDate
, lead(seq,1) over(order by seq desc) AS nextseq , lead(subject,1) over(order by seq desc) AS nextsubject
,fileName, orgFilename, fileSize, fk_subno from tbl_classnoti where fk_subno = #{fk_subno}
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
)B join tbl_person P on P.perno = B.fk_perno where B.seq = #{seq}

select previousseq, previoussubject , seq, fk_perno, subject, name, content, readcount, B.regDate as regDate , nextseq , nextsubject , fileName, orgFilename, fileSize, fk_subno from ( select lag(seq,1) over(order by seq desc) AS previousseq , lag(subject,1) over(order by seq desc) AS previoussubject
, seq, fk_perno, subject, content, readcount , to_char(regDate, 'yyyy-mm-dd') as regDate
, lead(seq,1) over(order by seq desc) AS nextseq , lead(subject,1) over(order by seq desc) AS nextsubject
,fileName, orgFilename, fileSize, fk_subno from tbl_classnoti where fk_subno = #{fk_subno}
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
)B join tbl_person P on P.perno = B.fk_perno where B.seq = #{seq}

select fileName
from tbl_classnoti
where seq = #{seq}
