mapper.select:
		
			
	     		select distinct ${searchType}
	     	
	     	
	     		select ${searchType}
	     	
   	    
		   from tbl_board B join tbl_person P
		   on B.fk_perno=P.perno
		   where status = 1
		   and lower(${searchType}) like '%'|| lower(#{searchWord}) ||'%'
		   
        	and categoryno = ${viewBoard}
           
		   
	      		order by seq desc

select count(*) from tbl_board B join tbl_person P on B.fk_perno=P.perno where status = 1
and lower(${searchType}) like '%'|| lower(#{searchWord}) ||'%'
and categoryno = ${viewBoard}

select rno,seq,categoryno,name,subject,good,readCount,commentCount, regDate ,fk_perno, fileName, groupno, fk_seq, depthno, namecheck from ( select rownum as rno, seq,categoryno,name,subject,good,readCount,commentCount, regDate ,fk_perno, fileName, groupno, fk_seq, depthno, namecheck from ( select B.seq, B.categoryno, P.name, B.subject, B.good, B.readCount, B.commentCount,to_char(B.regDate, 'yyyy-mm-dd hh24:mi:ss') as regDate ,fk_perno, B.fileName, groupno, fk_seq, depthno, namecheck from tbl_board B join tbl_person P on B.fk_perno=P.perno where status=1
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
and categoryno = ${viewBoard}
start with fk_seq=0 connect by prior seq=fk_seq order siblings by groupno desc, seq asc )V )T where rno between #{startRno} and #{endRno}

select comseq, categoryno, subject, content, name, reregdate, fk_seq, fk_perno from ( select row_number() over(order by comseq desc) as rno,C.fk_seq, comseq, categoryno, subject, C.content, name, to_char(reregdate, 'yyyy-mm-dd hh24:mi:ss') as reregdate, C.fk_perno from tbl_person P join tbl_comment C on C.fk_perno=P.perno join tbl_board B on C.fk_seq = B.seq where C.status = 1
and lower(C.${searchType}) like '%'||lower(#{searchWord})||'%'
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
and categoryno = ${viewBoard}
)V where rno between #{startRno} and #{endRno}

select C.${searchType}
select distinct ${searchType}
from tbl_person P join tbl_comment C on C.fk_perno=P.perno join tbl_board B on C.fk_seq = B.seq where C.status = 1
and lower(C.${searchType})
and lower(${searchType})
like '%'|| lower(#{searchWord}) ||'%'
and categoryno = ${viewBoard}
order by comseq desc
