mapper.select:
select fk_majseq, perno, name, mobile, email, content , rno, birthday from ( select fk_majseq, perno, name, mobile, email, content , birthday, row_number() over(order by perno desc) as rno from tbl_person p join tbl_major m on p.fk_majseq = m.MAJSEQ where idle = 0 and identity = 0
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
)v where rno between #{startRno} and #{endRno}

   		select perno, name, birthday, mobile, email, address
		from tbl_person
		where idle = 0 and identity = 0 and perno = #{PERNO}

select fk_majseq, perno, name, mobile, email, content , rno, birthday from ( select fk_majseq, perno, name, mobile, email, content , birthday, row_number() over(order by perno desc) as rno from tbl_person p join tbl_major m on p.fk_majseq = m.MAJSEQ where idle = 0 and identity = 1
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
)v where rno between #{startRno} and #{endRno}
