mapper.select:
select seq, subject, good, readCount, regDate, commentCount, name, namecheck , groupno, fk_seq, depthno , fileName, orgFilename, fileSize from ( select rownum AS rno, seq, subject, good, readCount, regDate, name , commentCount, namecheck , groupno, fk_seq, depthno,fileName, orgFilename, fileSize from ( select B.seq as seq, B.subject as subject, B.good as good, B.readCount as readCount, B.regDate as regDate, P.name as name , B.commentCount as commentCount, B.namecheck as namecheck , groupno, fk_seq, depthno,fileName, orgFilename, fileSize from ( select seq, subject, good, readCount, commentCount, fk_perno , to_char(regDate, 'yyyy-mm-dd') as regDate, categoryno, namecheck , groupno, fk_seq, depthno,fileName, orgFilename, fileSize from tbl_board where status = 1 and categoryno = #{categoryno}
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
start with fk_seq = 0 connect by prior seq = fk_seq order siblings by groupno desc, seq asc )B join tbl_person P on P.perno = B.fk_perno
order by good desc
)V ) where rno between #{startRno} and #{endRno}

select previousseq, previoussubject , seq, fk_perno, subject, name, content, readcount, B.regDate as regDate, good , nextseq , nextsubject, commentCount , groupno, fk_seq, depthno, fileName, orgFilename, fileSize, identity from ( select lag(seq,1) over(order by seq desc) AS previousseq , lag(subject,1) over(order by seq desc) AS previoussubject
, seq, fk_perno, subject, content, readcount, commentCount , to_char(regDate, 'yyyy-mm-dd') as regDate, good
, lead(seq,1) over(order by seq desc) AS nextseq , lead(subject,1) over(order by seq desc) AS nextsubject
, groupno, fk_seq, depthno, fileName, orgFilename, fileSize from tbl_board where status = 1
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
)B join tbl_person P on P.perno = B.fk_perno where B.seq = #{seq}

   		select ceil(count(*)/#{sizePerPage})
		from tbl_comment
		where status = 1 and fk_seq = #{fk_seq}

mapper.update:
update tbl_board set subject = #{subject}
                   , content = #{content}
where seq = #{seq}

mapper.select:
select good
from tbl_board
where seq = #{seq}
